using System;using LitJson;using UnityEngine;public class Bastion{	private int id;	private float lat;	private float lng;	private string name;	private int ownerid;	private string signature;	private int level;	private int camp;	private int status;	private int energy;	private int range;	private Drawer[] drawerSlots;	private Defence[] defenceSlots;	private Location _location;	private Bastion(JsonData data) {		id = int.Parse(data["id"].ToString());		lng = float.Parse(data["lng"].ToString());		lat = float.Parse (data["lat"].ToString());		name = data["name"].ToString().ToString();		ownerid = int.Parse(data["owner"].ToString());		signature = data["signature"].ToString();		level = int.Parse(data["level"].ToString());		camp = int.Parse(data["camp"].ToString());		status = int.Parse(data["status"].ToString());		energy = int.Parse(data["energy"].ToString());		range = int.Parse(data["range"].ToString());		_location = new Location (lng, lat);	}	public Location location {		get {			return _location;			}	}}