using System;using LitJson;using UnityEngine;public class Bastion{	private int id;	private double lat;	private double lng;	private string name;	private int ownerid;	private string signature;	private int level;	private int camp;	private int status;	private int energy;	private int range;	private Drawer[] drawerSlots;	private Defence[] defenceSlots;	private Lnglat point;	public Bastion(JsonData data) {		id = int.Parse(data["id"].ToString());		lng = double.Parse(data["lng"].ToString());		lat = double.Parse (data["lat"].ToString());		name = data["name"].ToString().ToString();		ownerid = int.Parse(data["owner"].ToString());		signature = data["signature"].ToString();		level = int.Parse(data["level"].ToString());		camp = int.Parse(data["camp"].ToString());		status = int.Parse(data["status"].ToString());		energy = int.Parse(data["energy"].ToString());		range = int.Parse(data["range"].ToString());		point = new Lnglat (lng, lat);	}	public Lnglat location {		get {			return point;			}	}}