using System;using LitJson;using UnityEngine;public class Bastion{	private int id;	private double lat;	private double lng;	private string name;	private int ownerid;	private string signature;	private int level;	private int camp;	private int status;	private int energy;	private int range;	private Drawer[] drawerSlots;	private Defence[] defenceSlots;	private Location _location;	private Bastion(JsonData data) {		id = int.Parse(data["id"].ToString());		lng = float.Parse(data["lng"]);		lat = float.Parse (data["lat"]);		name = data["name"].ToString();		ownerid = int.Parse(data["owner"].ToString());		signature = data["signature"];		level = int.Parse(data["level"]);		camp = int.Parse(data["camp"]);		status = int.Parse(data["status"]);		energy = int.Parse(data["energy"]);		range = int.Parse(data["range"]);		_location = new Location (lng, lat);	}	public Location location {		get {			return _location;			};	}}