using System;using UnityEngine;using LitJson;public class BastionManager:MonoBehaviour{	private Lnglat lastReqPoint = null;		public void RefreshBastion(Lnglat lnglat) {		if (lastReqPoint == null || !MapTile.CacheContains (lastReqPoint)) {			BastionNet.Instance.GetNearbyBastions (lnglat, OnGetNearbyBastions);		}	}	private void OnGetNearbyBastions(JsonData data) {		Debug.Log ("OnGetNearbyBastions:"+data.ToJson());		JsonData list = data ["list"];		for(int i = 0; i < list.Count; i++) {			Bastion bastion = new Bastion(list[i]);			MapTile tile = MapTile.FindFromCache(bastion.location);			if(tile != null) {				GameObject bastionPrefab = Resources.Load("Prefabs/ModelPrefab/Bastion") as GameObject;				GameObject bastionObj = GameObject.Instantiate (bastionPrefab) as GameObject;								if(tile.Quad != null) {					bastionObj.transform.parent = tile.Quad.transform;					bastionObj.transform.localPosition = tile.GetBastionPosition(bastion.location);					bastionObj.transform.localScale = new Vector3(0.025f, 0.025f, 0.025f);									}			}		}	}}