using System;using System.Collections.Generic;using UnityEngine;public class MapManager:MonoBehaviour{	private MapNet net;	private Texture2D curTexture;	public void Awake() {		net = new MapNet ();	}	public void RefreshMap(Lnglat lnglat) {		MapTile tile = MapTile.FindFromCache(lnglat);		if (tile != null) {			if (tile.IsDownloading) {				return;			} else {				ShowMapTile (tile);			}		} else {			tile = MapTile.LoadFromFile (lnglat);			if(tile != null) {				ShowMapTile(tile);			}			else {				StartCoroutine (net.DownloadMapTile (lnglat, OnMapTileDownloaded));			}		}	}	private void ShowMapTile(MapTile tile) {		GameObject obj = GameObject.Find ("Quad0");		if (obj != null) {			obj.renderer.material.mainTexture = tile.Texture;			tile.Quad = obj;		}	}	public void OnMapTileDownloaded(MapTile tile, int index) {				ShowMapTile (tile);		}}