using UnityEngine;using System.Collections;using LitJson;public class LocationManager:MonoBehaviour {	public void OnLocationUpdate(string data) {		Debug.Log ("OnLocationUpdate:"+data);		JsonData jdata = JsonMapper.ToObject (data);		float lat = float.Parse(jdata ["lat"].ToString());		float lng = float.Parse(jdata ["lng"].ToString());		if( Mathf.Abs(lat - Player.Instance.Latitude) > 0.00001f		   || Mathf.Abs(lng - Player.Instance.Longitude) > 0.00001f)		{			Player.Instance.Latitude = lat;			Player.Instance.Longitude= lng;						Bastion.Instance.GetNearbyBastions (Player.Instance.Longitude, Player.Instance.Latitude);			MapManager.Instance.RequestMap (Player.Instance.Longitude, Player.Instance.Latitude);		}	}}